// modified version - originally from https://gist.github.com/dceejay/cea8afa28b7a93ebdc0f 

// restartQueue = create new
if (msg.hasOwnProperty("restartQueue")) {
    context.queue = []

// if queue doesn't exist, create it    
} else {
    context.queue = context.queue || []
    context.busy = context.busy || false

    // if the msg is a trigger one release next message
    if (msg.hasOwnProperty("releaseNext")) {
        if (context.queue.length > 0) {
            var m = context.queue.shift()
            node.status({ fill: 'green', shape: 'dot', text: context.queue.length })
            return m 
        }
        else {
            context.busy = false;
        }
    }
    else {
        if (context.busy) {
            // if busy add to queue
            context.queue.push(msg);
        }
        else {
        // otherwise we are empty so just pass through and set busy flag
            context.busy = true;
            node.status({ fill: 'green', shape: 'dot', text: context.queue.length })
            return msg;
        }
    }
}
node.status({ fill: 'green', shape: 'dot', text: context.queue.length })

return null;

// checks existens of requried global variables
//
let value = global.get('system_baseUrl')
if (!isTruthyAndNotEmptyString(value)) {
    node.error('global - system_baseUrl not defined')
    return null
}
value = global.get('notification_cache')
if (!isTruthyAndNotEmptyString(value)) {
    node.error('global - notification_cache not defined')
    return null
}
value = global.get('notification_directory')
if (!isTruthyAndNotEmptyString(value)) {
    node.error('global - notification_directory not defined')
    return null
}
return msg

function isTruthyAndNotEmptyString (input) {
    return !(typeof input === 'undefined' || input === null ||
      (typeof input === 'number' && !Number.isFinite(input)) || input === '')
}
